package com.syntax.class02;

public class ModulusOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=10;
		int b=66;
		int c=a%b;
		System.out.println(c);
		
		float num1=3.98f;
		float num2=9.45f;
		float num3=num1%num2;
		System.out.println(num3);
	}

}
