package com.syntax.class03;

public class AddisionOrConcotenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		int a=10;
		int b=20;
		String c="Hello";
		String d="Hi";
		
		System.out.println(a+b+c+d);
		/// everything we concotenate with a string, it becomes a big string 
		System.out.println(c+d+a+b); 
		
		/// we put ()within () to show a arithematical operation when there is string+arthemical 
		System.out.println(c+d+(a+b));
		
		
		boolean boo=false;
		System.out.println(c+boo);
		
		
		
		
		
	}

}
