package com.syntax.class03;

public class PermitiveCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/// casting in java means converting one type to another type
		
		int i=100;
		double d=100;//// since the data type is double  so here the int value(100) is converted to double in console. 
		System.out.println(i);//// 100
		System.out.println(d);////100.0
			
		///type of casing : widening and narrowing 
		///changing a samll value to big is wedding 
		////changing big value to samll one is narrowing
		
		
		long k=100;
		float j=10000f;
		System.out.println(k);
		System.out.println(j);
		int x=(int)99.99;
		System.out.println(x);
		
		
	
		byte b=(byte)130;
		System.out.println(b);
		
		float t=(float)10.99f;
		System.out.println(t);
		
		float m=(float)34.98f;
		System.out.println(m);
			
		double u=(double)1.6;
		System.out.println(u);
			
	byte g=(byte)149;
	System.out.println(g);
	
	int f=(int)98.50;
	System.out.println(f);
	
	byte v=(byte)200;
	System.out.println(v);
	byte s=(byte)500;
	System.out.println(s);
	
	int h=(int)59999999.670;
	System.out.println(h);

	}

}
